mapper.select:
select a.*, t.name tenant_name, p.name product_name from ac_charge a left join ac_tenant t on a.tenant_id=t.id left join ac_product p on a.product_id=p.id
( t.name like concat('%', #{searchValue}, '%') or p.name like concat('%', #{searchValue}, '%') or quota = #{searchValue} )
and a.tenant_id=#{tenantId}
order by CONVERT(${order.key} USING gbk) ${order.value}

select c.*, t.name tenant_name, p.name product_name from ac_charge c 
left join ac_tenant t on c.tenant_id=t.id left join ac_product p on c.product_id=p.id
where c.id=#{id}
