mapper.select:
select * from ac_product
name like concat('%', #{searchValue}, '%') or code like concat('%', #{searchValue}, '%') or product_manager concat('%', #{searchValue}, '%') or version like concat('%', #{searchValue}, '%')
order by CONVERT(${order.key} USING gbk) ${order.value}

mapper.update:
update ac_product set name=#{name}, code=#{code}, version=#{version}, publish_time=#{publishTime}, product_manager=#{productManager}
, note=#{note}, status=#{status}, editor=#{editor}, edit_time=now()
where id=#{id}
