mapper.select:
select * from ac_tenant a
a.name like concat('%', #{searchValue}, '%') or a.code like concat('%', #{searchValue}, '%') or a.contact like concat('%', #{searchValue}, '%') or a.mobile like concat('%', #{searchValue}, '%') or a.phone like concat('%', #{searchValue}, '%') or a.email like concat('%', #{searchValue}, '%')
order by CONVERT(${order.key} USING gbk) ${order.value}

mapper.update:
update ac_tenant set name=#{name}, code=#{code}, contact=#{contact}, mobile=#{mobile}, phone=#{phone}, email=#{email}
, address=#{address}, logo=#{logo}, type=#{type}, note=#{note}, editor=#{editor} , edit_time=now()
where id=#{id}
